import { useRef, useState, useEffect } from 'react'
import './App.css'

const GRAVITY = 1

function App() {
  const intervalId = useRef<NodeJS.Timer | null>(null)
  const canvasRef = useRef<HTMLCanvasElement | null>(null)
  const [position, setPosition] = useState(230)
  const [score, setScore] = useState<number | null>(null)
  const [deltaY, setDeltaY] = useState(GRAVITY)
  const [startGame, setStartGame] = useState(false)
  const [gameOver, setGameOver] = useState(false)


  useEffect(() => {
    if (startGame) {
      setScore(0)
      intervalId.current = setInterval(() => runGame(), 24)
    }

    return () => {
      if (intervalId.current) {
        clearInterval(intervalId.current)
      }
    }
  }, [startGame, position, deltaY])

  const runGame = () => {
    if (canvasRef.current) {
      const ctx = canvasRef.current.getContext('2d')

      if (ctx) {
        const birdImage = document.getElementById('bird') as HTMLCanvasElement
        console.log(position)
        console.log(deltaY)
        ctx?.clearRect(0, 0, window.innerWidth, window.innerHeight)
        ctx?.drawImage(birdImage, 0, position, 20 * (14 / 9), 20)
      }
    }

    setPosition((prevPos) => prevPos - deltaY)
    setDeltaY((prevDelX) => prevDelX - GRAVITY)
  }

  const jump = () => {
    setDeltaY(GRAVITY * 10)
    setPosition((prevPos) => prevPos + deltaY)
  }

  const handleEvent = () => {
    if (!startGame) {
      setStartGame(true)
    }

    jump()
  }


  return (
    <div onKeyDown={handleEvent} onClick={handleEvent}>
      <img src={require('./images/bird.png')} id='bird' width='50' style={{ opacity: '0' }} />
      <canvas ref={canvasRef} width={500} height={500} />
      {gameOver && <div>GAME OVER</div>}
    </div>
  )
}

export default App
